$(document).ready(function () { $("#myCarousel").carousel({ interval: false }); $("body").on("click", ".aboutMe", function () { $('html, body').animate({ scrollTop: ($('#aboutMe').offset().top) }, 2500) }); $("body").on("click", ".skills", function () { $('html, body').animate({ scrollTop: ($('.skills-container').offset().top - 230) }, 2500) }); $("body").on("click", ".contact", function () { $('html, body').animate({ scrollTop: ($('footer').offset().top) }, 2500) }); $("body").on("click", ".logo", function () { $('html, body').animate({ scrollTop: ($('body').offset().top) }, 2500) }); var width = 120; var blur = 0; var newPos = 0; var lastPos = 0; var cutoffPos = 100000; $(window).scroll(function () { let imagePost = ""; if ($(window).width() <= 900) { imagePost = "-mobile"; } let scrollPos = $(this).scrollTop(); if (scrollPos < cutoffPos) { newPos = scrollPos / 3; let difference = newPos - lastPos; if (scrollPos < 1400) { $("#introduction").css({ "background-image": "linear-gradient(rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.8)),url(images/engine-background" + imagePost + ".jpg)" }); cutoffPos = 100000; width += difference; blur = width / 80; if (width < 120) { width = 120; blur = 0 } } else { $("#introduction").css({ "background-image": "linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0)),url(images/web-background" + imagePost + ".jpg)" }); width -= difference; blur = width / 80; if (width < 120) { cutoffPos = scrollPos; width = 120; } } $("#introduction").css({ "background-size": width + '%' }); $("#introduction .content-container").css({ "backdrop-filter": "blur(" + blur + "px)" }); lastPos = newPos; } }) });